class Product_category_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }

    function getRows()
    {
        $rs=$this->db->get("product_categories");

        return $rs->num_rows();
    }
    
    /*
     * Get product_category by id
     */
    function get_product_category($id)
    {
        return $this->db->get_where('product_categories',array('id'=>$id))->row_array();
    }
    
    /*
     * Get all product_categories
     */
    function get_all_product_categories()
    {
        return $this->db->get('product_categories')->result_array();
    }
    
    /*
     * function to add new product_category
     */
    function add_product_category($params)
    {
        $this->db->insert('product_categories',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update product_category
     */
    function update_product_category($id,$params)
    {
        $this->db->where('id',$id);
        $response = $this->db->update('product_categories',$params);
        if($response)
        {
            return "product_category updated successfully";
        }
        else
        {
            return "Error occuring while updating product_category";
        }
    }
    
    /*
     * function to delete product_category
     */
    function delete_product_category($id)
    {
        $response = $this->db->delete('product_categories',array('id'=>$id));
        if($response)
        {
            return "product_category deleted successfully";
        }
        else
        {
            return "Error occuring while deleting product_category";
        }
    }
}
